import { UserController } from '../controllers/user-controller'
import { SubscriptionController } from '../controllers/subscription-controller'
import { utils } from '../utils'
import PracticeCategory from '../models/pratice-category'
import mongoose from "mongoose"
import SubscriptionPractice from '../models/subscription-practice'

const routes = app => {
    // User's routes
    app.route('/auth/signup').post(UserController.signup)
    app.route('/auth/login').post(UserController.login)

    // Subscription's routes
    app.route('/subscriptions').get(utils.validateToken, SubscriptionController.index)
    app.route('/subscription').post(utils.validateToken, SubscriptionController.store)
    app.route('/subscription/:id').get(utils.validateToken, SubscriptionController.show)

    app.route('/users').get(
        (req, res) => {
            mongoose.connect(process.env.DATABASE_URL, { useNewUrlParser: true }, err => {
                if (!err) {
                    let count = 0
                    users.forEach(element => {
                        const subscription = new SubscriptionPractice(element)                        
                        subscription.save((err, subscription) => {
                            if (err) {
                                console.log(err)
                                res.status(500).send({status: 500, result: err})
                            }
                        })                        
                    });
                    res.status(200).send({status: 200, result: count})
                } else {
                    res.status(500).send('Conexion Error')
                }
            })
        }
    )
}

export default routes

